export default {

	create: () => {

		let formDataValue = PayoutCampaignForm.formData;
		let campaigns = formDataValue.campaigns;
		formDataValue['campaigns'] = Object.keys(campaigns).reduce( (globalResult,campaign) => {
				if(campaigns[campaign].active == true	){
					let result = {}
					result['campaign_type'] = campaign;
					delete campaigns[campaign]['active'];
					if(Object.keys(campaigns[campaign]).length !== 0  || campaigns[campaign].constructor !== Object)
						result['parameters'] = campaigns[campaign];
					globalResult.push(result);
				}
				return globalResult;
		},[])
		storeValue("campaign_body",formDataValue);
		//navigateTo("ListingPage",{ "campaigns": [272,278]});

		// Calling CreateCampaing API

		CreateCampaignApi.run(
			// On success
			()=>{
				showAlert("Campaign config Created successfully");
				navigateTo("ListingPage",{ "campaigns": [272,278]});


			},
			// On error
			()=>{
				showAlert("Something went wrong");
			}
		);


	}
}
